import React, { useState, useRef, useEffect } from "react";
import "./Form.css";

export default function Form() {
  let [name, setName] = useState("");
  let [username, setUsername] = useState("");
  let [email, setEmail] = useState("");
  let [mobileNumber, setMobileNumber] = useState(0);
  let [checkbox, setCheckbox] = useState(false);

  let warning1 = useRef();
  let warning2 = useRef();
  let warning3 = useRef();
  let warning4 = useRef();
  let warning5 = useRef();
  let status = [];
  let msg = [
    "Name can't be empty", 
    "UserName can't be empty",
    "Enter valid Email",
    "Enter valid 10 digit mobile number",
    "Please tick the checkbox"
  ]

  useEffect(() => {
    // if (name.trim().length < 1) status[0] = false;

    // if (name.trim().length >=  1) status[0] = true;

    // if (username.trim().length < 1) status[1] = false;

    // if (username.trim().length >= 1) status[1] = true;

    // if (isValidEmail(email) === false) status[2] = false;

    // if (isValidEmail(email) === true) status[2] = true;

    // if (isValidNumber(mobileNumber) === false) status[3] = false;

    // if (isValidNumber(mobileNumber) === true) status[3] = true;

    // if (checkbox === false) status[4] = false;

    // if (checkbox === true) status[4] = true;

  })

  let onClick = () => {

    // console.log(name.trim().length);
    // console.log(username);
    // console.log(email);
    // console.log(mobileNumber);
    // console.log(checkbox);

    // if (name.trim().length < 1){
    //   status[0] = false;
    // }

    // if (name.trim().length >=  1) status[0] = true;

    // if (username.trim().length < 1) status[1] = false;

    // if (username.trim().length >= 1) status[1] = true;

    // if (isValidEmail(email) === false) status[2] = false;

    // if (isValidEmail(email) === true) status[2] = true;

    // if (isValidNumber(mobileNumber) === false) status[3] = false;

    // if (isValidNumber(mobileNumber) === true) status[3] = true;

    // if (checkbox === false) status[4] = false;

    // if (checkbox === true) status[4] = true;

    if (name.trim().length >= 1 && username.trim().length >= 1 && 
    checkbox === true && isValidNumber(mobileNumber) && isValidEmail(email)){
      console.log("sucesss");
    }
    else{
      console.log("failure");
      // enableTheWarnings(status);
      if (name.trim().length < 1) warning1.current.innerText = msg[0];
      if (name.trim().length >= 1) warning1.current.style.display = "none";
    }

    console.log(status);
  }

  let enableTheWarnings = (status) => {
    for(let i = 0; i < status.length; i++){
      if (status[i] === false){
        if (i === 0) warning1.current.innerText = msg[i];
        else if (i === 1) warning2.current.innerText = msg[i];
        else if (i === 2) warning3.current.innerText = msg[i];
        else if (i === 3) warning4.current.innerText = msg[i];
        else if (i === 4) warning5.current.innerText = msg[i];
      }
      else{
        if (i === 0) warning1.current.style.display = "none";
        else if (i === 1) warning2.current.style.display = "none";
        else if (i === 2) warning3.current.style.display = "none";
        else if (i === 3) warning4.current.style.display = "none";
        else if (i === 4) warning5.current.style.display = "none";
      }
    }
  }

  let isValidNumber = (num) => {
    let len = Math.ceil(Math.log10(num + 1))-1;

    if (len === 10) return true;
    else return false;
  }

  let isValidEmail = (email) => {
    let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;    // eslint-disable-line
    if ( re.test(email) ) {
        return true;
    }

    return false;
  }

  return (
    <div className="form">
      <input
        type="text"
        className="name"
        placeholder="Name"
        onChange={(e) => {
          setName(e.target.value);
        }}
      />
      <p className="warning1" ref={warning1}></p>
      <input
        type="text"
        className="username"
        placeholder="UserName"
        onChange={(e) => {
          setUsername(e.target.value);
        }}
      />
      <p className="warning2" ref={warning2}></p>
      <input
        type="email"
        className="email"
        placeholder="Email"
        onChange={(e) => {
          setEmail(e.target.value);
        }}
      />
      <p className="warning3" ref={warning3}></p>
      <input
        type="text"
        className="mobileNumber"
        placeholder="Mobile"
        onChange={(e) => {
          setMobileNumber(e.target.value);
        }}
      />
      <p className="warning4" ref={warning4}></p>
      <div className="checkAndCondtions">
        <input
          type="checkbox"
          name=""
          id=""
          className="checkbox"
          onChange={(e) => {
            setCheckbox(e.target.checked);
          }}
        />
        <p className="acceptCondtions">
          Share my registration data with Superapp
        </p>
      </div>
      <p className="warning5" ref={warning5}></p>
      <button className="btn-sign-up" onClick={onClick}>SIGN UP</button>
    </div>
  );
}
