import React, { useState } from "react";
import "./Timer.css";

export default function Timer() {
  let [hours, setHours] = useState("00");
  let [minutes, setMinutes] = useState("00");
  let [seconds, setSeconds] = useState("00");

  let increment_hour = () => {
    let temp = Number(hours);
    temp++;
    if (temp <= 9) setHours("0" + temp);
    else setHours(temp);
  };
  let increment_minute = () => {
    let temp = Number(minutes);
    temp++;
    if (temp <= 9) setMinutes("0" + temp);
    else setMinutes(temp);
  };
  let increment_second = () => {
    let temp = Number(seconds);
    temp++;
    if (temp <= 9) setSeconds("0" + temp);
    else setSeconds(temp);
  };

  let decrement_hour = () => {
    let temp = Number(hours);
    if (temp >= 1){
        temp--;
        if (temp <= 9) setHours("0" + temp);
        else setHours(temp);
    }
  }

  let decrement_minute = () => {
    let temp = Number(minutes);
    if (temp >= 1){
        temp--;
        if (temp <= 9) setMinutes("0" + temp);
        else setMinutes(temp);
    }
  }

  let decrement_second = () => {
    let temp = Number(seconds);
    if (temp >= 1){
        temp--;
        if (temp <= 9) setSeconds("0" + temp);
        else setSeconds(temp);
    }
  }

  return (
    <div className="timer-card">
      <div className="time-circle"></div>
      <div className="time-show">
        <div className="set-time">
          <div className="hours">
            <p className="hours">Hours</p>
            <div className="wrapper-time">
              <img
                src="up.png"
                alt=""
                className="up-icon"
                onClick={increment_hour}
              />
              <p className="hours-time">{hours}</p>
              <img src="down.png" alt="" className="down-icon" onClick={decrement_hour}/>
            </div>
          </div>
          <p className="colon">:</p>
          <div className="minutes">
            <p className="minutes">Minutes</p>
            <div className="wrapper-time">
              <img
                src="up.png"
                alt=""
                className="up-icon"
                onClick={increment_minute}
              />
              <p className="hours-time">{minutes}</p>
              <img src="down.png" alt="" className="down-icon" onClick={decrement_hour}/>
            </div>
          </div>
          <p className="colon">:</p>
          <div className="seconds">
            <p className="seconds">Seconds</p>
            <div className="wrapper-time">
              <img
                src="up.png"
                alt=""
                className="up-icon"
                onClick={increment_second}
              />
              <p className="hours-time">{seconds}</p>
              <img src="down.png" alt="" className="down-icon" />
            </div>
          </div>
        </div>
        <div className="start-button">
          <button className="start">Start</button>
        </div>
      </div>
    </div>
  );
}
