import React, { useState } from "react";
import "./Form.css";

export default function Form() {
  let [name, setName] = useState("");
  let [username, setUsername] = useState("");
  let [email, setEmail] = useState("");
  let [mobileNumber, setMobileNumber] = useState(0);
  let [checkbox, setCheckbox] = useState(false);

  let onClick = () => {
    // console.log(name);
    // console.log(username);
    // console.log(email);
    // console.log(mobileNumber);
    // console.log(checkbox);

    if (name.trim().length >= 1 && username.trim().length >= 1 && 
    checkbox === true && isValidNumber(mobileNumber) && isValidEmail(email)){
      console.log("sucesss");
    }
    else{
      console.log("failure");
    }
  }

  let isValidNumber = (num) => {
    let len = Math.ceil(Math.log10(num + 1))-1;
    console.log(len);

    if (len === 10) return true;
    else return false;
  }

  let isValidEmail = (email) => {
    let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    if ( re.test(email) ) {
        // this is a valid email address
        // call setState({email: email}) to update the email
        // or update the data in redux store.
        return true;
    }
    else {
        // invalid email, maybe show an error to the user.
        return fa
    }
  }

  return (
    <div className="form">
      <input
        type="text"
        className="name"
        placeholder="Name"
        onChange={(e) => {
          setName(e.target.value);
        }}
      />
      <input
        type="text"
        className="username"
        placeholder="UserName"
        onChange={(e) => {
          setUsername(e.target.value);
        }}
      />
      <input
        type="email"
        className="email"
        placeholder="Email"
        onChange={(e) => {
          setEmail(e.target.value);
        }}
      />
      <input
        type="text"
        className="mobileNumber"
        placeholder="Mobile"
        onChange={(e) => {
          setMobileNumber(e.target.value);
        }}
      />

      <div className="checkAndCondtions">
        <input
          type="checkbox"
          name=""
          id=""
          className="checkbox"
          onChange={(e) => {
            setCheckbox(e.target.checked);
          }}
        />
        <p className="acceptCondtions">
          Share my registration data with Superapp
        </p>
      </div>
      <button className="btn-sign-up" onClick={onClick}>SIGN UP</button>
    </div>
  );
}
