import React, { useState } from "react";
import "./Timer.css";

export default function Timer() {
  let [hours, setHours] = useState("00");
  let [minutes, setMinutes] = useState("00");
  let [seconds, setSeconds] = useState("00");

  let increment = (caption) => {
    if (caption === "hour") {
      let temp = Number(hours);
      temp++;
      if (temp <= 9) setHours("0" + temp);
      else setHours(temp);
    }
  };
  return (
    <div className="timer-card">
      <div className="time-circle"></div>
      <div className="time-show">
        <div className="set-time">
          <div className="hours">
            <p className="hours">Hours</p>
            <div className="wrapper-time">
              <img
                src="up.png"
                alt=""
                className="up-icon"
                onClick={(hour) => onClick()}
              />
              <p className="hours-time">{hours}</p>
              <img src="down.png" alt="" className="down-icon" />
            </div>
          </div>
          <p className="colon">:</p>
          <div className="minutes">
            <p className="minutes">Minutes</p>
            <div className="wrapper-time">
              <img src="up.png" alt="" className="up-icon" />
              <p className="hours-time">{minutes}</p>
              <img src="down.png" alt="" className="down-icon" />
            </div>
          </div>
          <p className="colon">:</p>
          <div className="seconds">
            <p className="seconds">Seconds</p>
            <div className="wrapper-time">
              <img src="up.png" alt="" className="up-icon" />
              <p className="hours-time">{seconds}</p>
              <img src="down.png" alt="" className="down-icon" />
            </div>
          </div>
        </div>
        <div className="start-button">
          <button className="start">Start</button>
        </div>
      </div>
    </div>
  );
}
