      <div className="movies-genres-img-title">
        {genre.map((option) => (
          <>
            <p className="title-genre">{option}</p>
            <div className="movies-img">
              <img src={url_action[0]} alt="" className="img-one-by-one" />
              <img src={url_action[1]} alt="" className="img-one-by-one" />
              <img src={url_action[2]} alt="" className="img-one-by-one" />
