import React, {useState} from "react";
import "./Form.css";

export default function Form() {
  let [name,setName] = useState("");
  let [username,setUsername] = useState("");
  let [email,setEmail] = useState("");
  let [mobileNumber,setMobileNumber] = useState(0);
  let [checkbox, setCheckbox] = useState(false);

  return (
    <div className="form">
      <input type="text" className="name" placeholder="Name" onChange={(e) => {setName(e.target.value)}} />
      <input type="text" className="username" placeholder="UserName" onChange={(e) => {setName(e.target.value)}}/>
      <input type="text" className="email" placeholder="Email" />
      <input type="text" className="mobileNumber" placeholder="Mobile" />

      <div className="checkAndCondtions">
        <input type="checkbox" name="" id="" className="checkbox" />
        <p className="acceptCondtions">
          Share my registration data with Superapp
        </p>
      </div>
      <button className="btn-sign-up">SIGN UP</button>
    </div>
  );
}
