import React from "react";
import "./LowerHalfBrowse.css";
import axios from "axios";

let allUrls = [];
let genre = JSON.parse(localStorage.getItem("categoriesOfUsers"));

async function get(type){
    let img_urls = []; 
    const options = {
      method: 'GET',
      url: 'https://online-movie-database.p.rapidapi.com/title/v2/find',
      params: {
        title: 'a',
        titleType: 'movie',
        limit: '4',
        sortArg: 'moviemeter,asc',
        genre: type,
        releaseDateMin: '2022-01-01',
        primaryLanguage: 'en'
      },
      headers: {
        'X-RapidAPI-Key': '2d70ca2dd4mshfa32868c99c1644p19e792jsnfb8f824a87bb',
        'X-RapidAPI-Host': 'online-movie-database.p.rapidapi.com'
      }
    };

    let response = await axios.request(options);
    let d = response.data;
    for(let i = 0; i < 4; i++){
      img_urls[i] = d.results[i].image.url;
    }

    // console.log(type,img_urls);
    return img_urls;
  }

  let onLoad = () => {
    console.log("entered");
    for(let i =0 ;i < genre.length; i++){
        allUrls[i] = get(genre[i]);
    }
    console.log(allUrls);
  }

export default function LowerHalfBrowse() {
  return (
    <div className="lower-browse" onLoad={onLoad()}>
      <p className="caption-browse">Entertainment according to your choice</p>

      <div className="movies-genres-img-title">
        {genre.map((option) => (
          <>
            <p className="title-genre">{option}</p>
            <div className="movies-img">

                {/* <img src={urls} alt="" className="img-one-by-one" /> */}
            </div>
          </>
        ))}
      </div>
    </div>
  );
}
