import React, { useState, useEffect } from 'react';
import './RightHomePage.css';
import axios from 'axios';

function RightHomePage() {
  // const api_key_news = 'cc9872637464488daad0dd749a016dae';
  const api_key_news = 'eb48da38f71b43adb91ed12f2ea452d3';

  let [news, setNews] = useState([]);

  async function getNews(){
    try{
      let response = await axios.get(`https://newsapi.org/v2/everything?domains=techcrunch.com,thenextweb.com&pageSize=2&apiKey=${api_key_news}`);
      let d = response.data;
      let title = d.articles[1].title;
      let desc = d.articles[1].description;
      let urlImage = d.articles[1].urlToImage;
      let urlNews = d.articles[1].url;
      console.log([title,desc,urlImage,urlNews]);
      setNews([title,desc,urlImage,urlNews]);
    }
    catch(err){
      console.log(err);
    }
  }
  useEffect(()=> {
    getNews();
  },[news]);

  let onClick = () => {
    window.location.replace("www.google.com");   
  }
  return (
    <div className="news-card" onClick={() => onClick}>
      <div className="news-image">
        <img src={news[2]} alt="" className="img" />
      </div>
      <div className="news-overview-caption">
        <p className="caption-news">{news[0]}</p>
        <p className="dateTime">2-20-2023 | 07:35 PM</p>
      </div>
      <div className="news-overview">
        <p className="news-content">{news[1]}</p>
      </div>
    </div>
  )
}

export default  RightHomePage;
